import React from "react";
import "./ControlButtons.css";

const ControlButtons = (props) => {

    const StartButton = (
        <div>
            <button className="btn" onClick={props.handleStart}>Start</button>
            {/* <div className="input">
                <input className="input" disabled={true} type="text" placeholder="Type The Text Here" />
            </div> */}
        </div>
    );
    const PauseButton = (
        <div className="btn">
            <div className="btn btn-one" onClick={props.handlePauseResume}> {props.isPaused ? "Resume" : "Pause"} </div>
        </div>
    );

    return (
        <div className="btn">
            <div>{props.active ? PauseButton : StartButton}</div>
        </div>
    );
}

export default ControlButtons
