import "./ControlButtons.css";

const ControlButtons=(props)=> {
    <div className="btn">

    const StartButton = (
        <div onClick={props.handleStart}>Start</div>
    );
    const PauseButton = (
        {/* <div className="btn"> */}
            <div className="btn btn-one" onClick={props.handlePauseResume}> {props.isPaused ? "Resume" : "Pause"} </div>
        {/* </div> */}
    );

    return (
        {/* // <div className="btn"> */}
            <div>{props.active ? PauseButton : StartButton}</div>
    );
     </div >
}

