import React,{useState} from "react";
import "./ControlButtons.css";

const ControlButtons = (props) => {

    const [buttonState, setButtonState] = useState(initialState)

    const handleStart=()=>{
        props.handleStart()
    }

    const StartButton = (
        <div className="btn" onClick={handleStart}>Start</div>
    );
    const PauseButton = (
        <div className="btn" onClick={props.handlePauseResume}> {props.isPaused ? "Resume" : "Pause"} </div>
    );

    return (
        <div>
            <div className="btn">{props.active ? PauseButton : StartButton}</div>
            <input className='input' disabled={true} type="text" placeholder="Type The Text Here" />
        </div>
    );
}

export default ControlButtons
